ALTER VIEW [dbo].[viewProductExportUpdated2]\nAS\nWITH ProductsWithXml AS (\n  SELECT \n    ProductCategoryID,\n    ProductCategoryName,\n    ShortDescription,\n    LongDescription,\n    CAST(ProductXML AS XML) AS ProdXml\n  FROM \n    dbo.Products\n)\nSELECT \n  pc.ProductCategoryID AS ProductCode,\n  pc.ProductCategoryName AS ProductTitle,\n  p.ShortDescription AS ProductShortDesc,\n  p.LongDescription AS ProductLongDesc,\n  p.ProdXml.value('data(Products/PackSize)[1]', 'nvarchar(max)') AS ProductPackSize\nFROM \n  dbo.ProductCategories pc\n  INNER JOIN ProductsWithXml p ON pc.ProductCategoryID = p.ProductCategoryID\nWHERE \n  pc.Deleted = 0\nGO