CREATE TABLE owners (\n  id INT PRIMARY KEY,\n  name VARCHAR(255)\n);\n\nCREATE TABLE cars (\n  id INT PRIMARY KEY,\n  owner_id INT,\n  FOREIGN KEY (owner_id) REFERENCES owners(id)\n);\n\nCREATE TABLE car_owner_history (\n  id INT PRIMARY KEY,\n  car_id INT,\n  owner_id INT,\n  start_date DATETIME,\n  FOREIGN KEY (car_id) REFERENCES cars(id),\n  FOREIGN KEY (owner_id) REFERENCES owners(id)\n);