CREATE PROCEDURE Procedure_All\nAS\nBEGIN\n    DECLARE @ID INT;\n    BEGIN TRANSACTION;\n\n    BEGIN TRY\n        -- Insert data into Table1\n        EXEC Procedure1;\n        SET @ID = SCOPE_IDENTITY();\n\n        -- Insert data into Table2\n        EXEC Procedure2 @ID;\n\n        -- Insert data into Table3\n        EXEC Procedure3 @ID;\n\n        COMMIT TRANSACTION;\n    END TRY\n    BEGIN CATCH\n        ROLLBACK TRANSACTION;\n        THROW; -- Re-throw the exception\n    END CATCH;\nEND;