CREATE TABLE Site (\n  Reference CHAR(5) PRIMARY KEY,\n  Name VARCHAR(255),\n  Charge DECIMAL(10, 2)\n);\n\nCREATE TABLE Residency (\n  SiteReference CHAR(5) REFERENCES Site(Reference),\n  ResidentReference CHAR(3),\n  PRIMARY KEY (SiteReference, ResidentReference)\n);\n\nCREATE TABLE Resident (\n  SiteReference CHAR(5),\n  ResidentReference CHAR(3),\n  Address VARCHAR(255),\n  Username VARCHAR(255),\n  Password VARCHAR(255),\n  PRIMARY KEY (SiteReference, ResidentReference),\n  FOREIGN KEY (SiteReference, ResidentReference) REFERENCES Residency(SiteReference, ResidentReference)\n);