-- Drop existing primary key\nALTER TABLE YourTableName\nDROP CONSTRAINT PK_YourTableName_OldPrimaryKey;\n\n-- Create new primary key\nALTER TABLE YourTableName\nADD CONSTRAINT PK_YourTableName_GuidId PRIMARY KEY (GuidId);\n\n-- Drop old foreign key constraint\nALTER TABLE YourReferencingTableName\nDROP CONSTRAINT FK_YourReferencingTableName_OldForeignKey;\n\n-- Add new foreign key constraint\nALTER TABLE YourReferencingTableName\nADD CONSTRAINT FK_YourReferencingTableName_NewForeignKey\nFOREIGN KEY (NewForeignKey) REFERENCES YourTableName(GuidId);