CREATE TABLE Films (\n  filmid INT PRIMARY KEY AUTO_INCREMENT,\n  filmtitle VARCHAR(255) NOT NULL,\n  runningtime INT,\n  description TEXT\n);\n\nCREATE TABLE Years (\n  yearid INT PRIMARY KEY,\n  year INT UNIQUE NOT NULL\n);\n\nCREATE TABLE Genres (\n  genreid INT PRIMARY KEY,\n  genre VARCHAR(50) UNIQUE NOT NULL\n);\n\nCREATE TABLE Directors (\n  directorid INT PRIMARY KEY,\n  director VARCHAR(100) UNIQUE NOT NULL\n);\n\nCREATE TABLE Actors (\n  actorid INT PRIMARY KEY,\n  actor_name VARCHAR(100) UNIQUE NOT NULL\n);\n\nCREATE TABLE Film_Genres (\n  filmid INT,\n  genreid INT,\n  PRIMARY KEY (filmid, genreid),\n  FOREIGN KEY (filmid) REFERENCES Films(filmid),\n  FOREIGN KEY (genreid) REFERENCES Genres(genreid)\n);\n\nCREATE TABLE Film_Directors (\n  filmid INT,\n  directorid INT,\n  PRIMARY KEY (filmid, directorid),\n  FOREIGN KEY (filmid) REFERENCES Films(filmid),\n  FOREIGN KEY (directorid) REFERENCES Directors(directorid)\n);\n\nCREATE TABLE Film_Actors (\n  filmid INT,\n  actorid INT,\n  PRIMARY KEY (filmid, actorid),\n  FOREIGN KEY (filmid) REFERENCES Films(filmid),\n  FOREIGN KEY (actorid) REFERENCES Actors(actorid)\n);\n\nCREATE TABLE Film_Years (\n  filmid INT,\n  yearid INT,\n  PRIMARY KEY (filmid, yearid),\n  FOREIGN KEY (filmid) REFERENCES Films(filmid),\n  FOREIGN KEY (yearid) REFERENCES Years(yearid)\n);