-- Create a linked server\nEXEC sp_addlinkedserver \n    @server='LinkedServerName', \n    @srvproduct='SQL Server', \n    @provider='SQLNCLI', \n    @datasrc='DestinationServerName'\n\n-- Create a login mapping\nEXEC sp_addlinkedsrvlogin \n    @rmtsrvname='LinkedServerName', \n    @useself='FALSE', \n    @locallogin='YourLocalLogin', \n    @rmtuser='YourRemoteLogin', \n    @rmtpassword='YourRemotePassword'\n\n-- Now you can query the linked server\nINSERT INTO LinkedServerName.db2.table2\nSELECT * \nFROM db1.table1\nWHERE rule1 = true