IF EXISTS\n    (\n    SELECT 1\n    FROM Timesheet_Hours\n    WHERE Posted_Flag = 1\n    AND Staff_Id = @PersonID\n    )\n    BEGIN\n        THROW 51000, 'Timesheets have already been posted!', 1\n    END\n\nIF NOT EXISTS\n    (\n    SELECT 1\n    FROM Timesheet_Hours\n    WHERE Staff_Id = @PersonID\n    )\n    BEGIN\n        THROW 51000, 'Default list has not been loaded!', 1\n    END