DECLARE @tableName sysname = 'YourTableName';\nDECLARE @sql nvarchar(max) = '';\n\nSELECT @sql += '\n    DECLARE @' + c.name + ' ' + t.name + ';\n    SET @' + c.name + ' = ' + \n    CASE \n        WHEN t.name IN ('int', 'bigint') THEN 'ABS(CHECKSUM(NEWID()))'\n        WHEN t.name IN ('nvarchar', 'varchar') THEN ''' + c.name + '_' + CONVERT(varchar(10), ABS(CHECKSUM(NEWID()))) + ''''\n        WHEN t.name IN ('datetime', 'datetime2') THEN 'DATEADD(day, ABS(CHECKSUM(NEWID())) % 365, ''2000-01-01'')'\n        WHEN t.name = 'bit' THEN 'ABS(CHECKSUM(NEWID())) % 2'\n        ELSE 'NULL'\n    END + ';\n    INSERT INTO ' + @tableName + ' (' + c.name + ') VALUES (@' + c.name + ');'\nFROM sys.columns c\nJOIN sys.types t ON c.system_type_id = t.system_type_id\nWHERE c.object_id = OBJECT_ID(@tableName);\n\nEXEC sp_executesql @sql;