INSERT INTO aspnet_role_ordering (RoleId, DisplayOrder)\nSELECT RoleId, DisplayOrder\nFROM (\n    SELECT RoleId, 1 AS DisplayOrder\n    FROM aspnet_roles\n    WHERE RoleName = 'Admin'\n    UNION\n    SELECT RoleId, 2 AS DisplayOrder\n    FROM aspnet_roles\n    WHERE RoleName = 'Moderator'\n    UNION\n    SELECT RoleId, 3 AS DisplayOrder\n    FROM aspnet_roles\n    WHERE RoleName = 'Member'\n) AS RoleOrdering;