CREATE PROCEDURE sp_PlaceOrder\n    @OrderId INT,\n    @CustomerId INT,\n    @OrderDate DATETIME,\n    @Total DECIMAL(10, 2)\nAS\nBEGIN\n    BEGIN TRANSACTION\n\n    BEGIN TRY\n        INSERT INTO Orders (OrderId, CustomerId, OrderDate, Total)\n        VALUES (@OrderId, @CustomerId, @OrderDate, @Total)\n\n        UPDATE Inventory\n        SET Quantity = Quantity - 1\n        WHERE ProductId = @ProductId\n\n        COMMIT TRANSACTION\n    END TRY\n    BEGIN CATCH\n        ROLLBACK TRANSACTION\n        RAISERROR ('Error placing order', 16, 1)\n    END CATCH\nEND