IF EXISTS\n    (\n    SELECT 1\n    FROM Timesheet_Hours\n    WHERE Posted_Flag = 1\n    AND Staff_Id = @PersonID\n    )\n    BEGIN\n        RAISERROR('Timesheets have already been posted!', 16, 1)\n        ROLLBACK TRAN\n    END\n\nIF NOT EXISTS\n    (\n    SELECT 1\n    FROM Timesheet_Hours\n    WHERE Staff_Id = @PersonID\n    )\n    BEGIN\n        RAISERROR('Default list has not been loaded!', 16, 1)\n        ROLLBACK TRAN\n    END