DECLARE @sql AS NVARCHAR(MAX);\nDECLARE @pivotList AS NVARCHAR(MAX);\n\nSELECT @pivotList = COALESCE(@pivotList + ',', '') + QUOTENAME(Type)\nFROM YourTable\nGROUP BY Type;\n\nSET @sql = N'\n    SELECT \n        [Action], ' + @pivotList + '\n    FROM \n        (SELECT \n             [Action],\n             [Type]\n         FROM \n             YourTable) AS SourceTable\n    PIVOT \n        (MAX([Type]) \n         FOR [Type] IN (' + @pivotList + ')) AS PivotTable;';\n\nEXEC sp_executesql @sql;