SELECT DISTINCT Source.ItemName,\n    CASE WHEN Count(SELECT Source.DateAdded FROM Source WHERE Int(Date()-Source.DateAdded) > 20) IS NULL THEN 0 ELSE Count(SELECT Source.DateAdded FROM Source WHERE Int(Date()-Source.DateAdded) > 20) END AS Total,\n    CASE WHEN Count(SELECT Source.DateAdded FROM Source WHERE Int(Date()-Source.DateAdded) BETWEEN 20 AND 44) IS NULL THEN 0 ELSE Count(SELECT Source.DateAdded FROM Source WHERE Int(Date()-Source.DateAdded) BETWEEN 20 AND 44) END AS BTWN_20_44,\n    CASE WHEN Count(SELECT Source.DateAdded FROM Source WHERE Int(Date()-Source.DateAdded) BETWEEN 45 AND 60) IS NULL THEN 0 ELSE Count(SELECT Source.DateAdded FROM Source WHERE Int(Date()-Source.DateAdded) BETWEEN 45 AND 60) END AS BTWN_45_60,\n    CASE WHEN Count(SELECT Source.DateAdded FROM Source WHERE Int(Date()-Source.DateAdded) BETWEEN 61 AND 90) IS NULL THEN 0 ELSE Count(SELECT Source.DateAdded FROM Source WHERE Int(Date()-Source.DateAdded) BETWEEN 61 AND 90) END AS BTWN_61_90,\n    CASE WHEN Count(SELECT Source.DateAdded FROM Source WHERE Int(Date()-Source.DateAdded) BETWEEN 91 AND 180) IS NULL THEN 0 ELSE Count(SELECT Source.DateAdded FROM Source WHERE Int(Date()-Source.DateAdded) BETWEEN 91 AND 180) END AS BTWN_91_180,\n    CASE WHEN Count(SELECT Source.DateAdded FROM Source WHERE Int(Date()-Source.DateAdded) > 180) IS NULL THEN 0 ELSE Count(SELECT Source.DateAdded FROM Source WHERE Int(Date()-Source.DateAdded) > 180) END AS GT_180\nFROM Source\nGROUP BY Source.ItemName;