SELECT \n  (SELECT COUNT(TBL_PROPERTY.PROPREF) \n   FROM TBL_PROPERTY \n   INNER JOIN TBL_REPAIR_ORDER ON TBL_PROPERTY.PROPREF = TBL_REPAIR_ORDER.PROPREF \n   INNER JOIN TBL_REPAIR_VISIT ON TBL_REPAIR_ORDER.ORDERID = TBL_REPAIR_VISIT.ORDERID \n   INNER JOIN tbl_contract ON tbl_repair_order.CONTRACT = tbl_contract.CONTRACT \n   WHERE (TBL_CONTRACT.CONTRACT IN ('STE')) AND \n         (TBL_REPAIR_ORDER.RAISEDDATE BETWEEN '01/12/2008' AND DATEADD(hh,23,'01/01/2009'))) AS TotalOrders,\n  (SELECT COUNT(TBL_PROPERTY.PROPREF) \n   FROM TBL_PROPERTY \n   INNER JOIN TBL_REPAIR_ORDER ON TBL_PROPERTY.PROPREF = TBL_REPAIR_ORDER.PROPREF \n   INNER JOIN TBL_REPAIR_VISIT ON TBL_REPAIR_ORDER.ORDERID = TBL_REPAIR_VISIT.ORDERID \n   INNER JOIN tbl_contract ON tbl_repair_order.CONTRACT = tbl_contract.CONTRACT \n   WHERE (TBL_CONTRACT.CONTRACT IN ('STE')) AND \n         (TBL_REPAIR_ORDER.RAISEDDATE BETWEEN '01/12/2008' AND DATEADD(hh,23,'01/01/2009')) AND \n         TBL_REPAIR_ORDER.STATUS <> 'Completed') AS OrdersIncomplete