-- Create a script to create the table structure\nSELECT OBJECT_DEFINITION(OBJECT_ID) AS CreateTableScript\nFROM sys.tables\nWHERE name = 'YourTableName'\n\n-- Create a script to insert the data\nSELECT 'INSERT INTO YourTableName (column1, column2, ...) VALUES (' + \n       CONVERT(VARCHAR(MAX), column1) + ', ' + \n       CONVERT(VARCHAR(MAX), column2) + ', ...)'\nFROM YourTableName