SELECT \n    t.name AS TableName,\n    CASE WHEN (SELECT COUNT(*) FROM syspermissions p WHERE p.id = t.id AND p.uid = u.uid AND p.actadd & 1 = 1) > 0 THEN 'Yes' ELSE 'No' END AS SELECT_Permission,\n    CASE WHEN (SELECT COUNT(*) FROM syspermissions p WHERE p.id = t.id AND p.uid = u.uid AND p.actadd & 2 = 2) > 0 THEN 'Yes' ELSE 'No' END AS DELETE_Permission,\n    CASE WHEN (SELECT COUNT(*) FROM syspermissions p WHERE p.id = t.id AND p.uid = u.uid AND p.actadd & 4 = 4) > 0 THEN 'Yes' ELSE 'No' END AS UPDATE_Permission,\n    CASE WHEN (SELECT COUNT(*) FROM syspermissions p WHERE p.id = t.id AND p.uid = u.uid AND p.actadd & 8 = 8) > 0 THEN 'Yes' ELSE 'No' END AS INSERT_Permission,\n    CASE WHEN (SELECT COUNT(*) FROM syspermissions p WHERE p.id = t.id AND p.uid = u.uid AND p.actadd & 16 = 16) > 0 THEN 'Yes' ELSE 'No' END AS ALTER_Permission\nFROM \n    sysobjects t\nINNER JOIN \n    sysusers u ON u.uid = t.uid\nWHERE \n    u.name = 'YourUsername'  -- Replace with the desired username\nORDER BY \n    t.name