-- Create a table to store the waveform data\nCREATE TABLE waveforms (\n  id NUMBER PRIMARY KEY,\n  data BLOB\n);\n\n-- Insert a sample waveform into the table\nDECLARE\n  waveform_data BINARY_FLOAT := 1.0;\n  l_blob BLOB;\nBEGIN\n  FOR i IN 1..10000 LOOP\n    waveform_data := waveform_data + i;\n    l_blob := l_blob || UTL_RAW.CAST_FROM_BINARY_FLOAT(waveform_data);\n  END LOOP;\n  INSERT INTO waveforms (id, data) VALUES (1, l_blob);\nEND;\n\n-- Define a PL/SQL function to calculate the maximum value in the waveform\nCREATE OR REPLACE FUNCTION get_max_value(p_id NUMBER)\n  RETURN NUMBER\nIS\n  l_blob BLOB;\n  l_max_value BINARY_FLOAT := -999999999;\nBEGIN\n  SELECT data INTO l_blob FROM waveforms WHERE id = p_id;\n  FOR i IN 0..(DBMS_LOB.GETLENGTH(l_blob) / 4) - 1 LOOP\n    l_max_value := GREATEST(l_max_value, UTL_RAW.CAST_TO_BINARY_FLOAT(DBMS_LOB.SUBSTR(l_blob, 4, i * 4 + 1)));\n  END LOOP;\n  RETURN l_max_value;\nEND;\n\n-- Call the function to get the maximum value\nSELECT get_max_value(1) FROM DUAL;