CREATE FUNCTION GetWeekDates (@Year INT, @WeekNumber INT)\nRETURNS @Dates TABLE (StartDate DATE, EndDate DATE)\nAS\nBEGIN\n    DECLARE @FirstDayOfYear DATE = DATEFROMPARTS(@Year, 1, 1)\n    DECLARE @FirstDayOfWeek DATE = DATEADD(wk, @WeekNumber - 1, @FirstDayOfYear)\n\n    DECLARE @StartDate DATE = @FirstDayOfWeek\n    DECLARE @EndDate DATE = DATEADD(dd, 6, @StartDate)\n\n    INSERT INTO @Dates (StartDate, EndDate)\n    VALUES (@StartDate, @EndDate)\n\n    RETURN\nEND