DECLARE @sortOrder nvarchar(10) = 'DESC';\n\nSELECT * FROM #t_results\nORDER BY\n    CASE WHEN @OrderBy = 'surname'           THEN surname END @sortOrder,\n    CASE WHEN @OrderBy = 'forename'          THEN forename END @sortOrder,\n    CASE WHEN @OrderBy = 'fullName'          THEN fullName END @sortOrder,\n    CASE WHEN @OrderBy = 'userId'            THEN userId END @sortOrder,\n    CASE WHEN @OrderBy = 'MobileNumber'      THEN MSISDN END @sortOrder,\n    CASE WHEN @OrderBy = 'DeviceStatus'      THEN DeviceStatus END @sortOrder,\n    CASE WHEN @OrderBy = 'LastPosition'      THEN LastPosition END @sortOrder,\n    CASE WHEN @OrderBy = 'LastAlert'         THEN LastAlert END @sortOrder,\n    CASE WHEN @OrderBy = 'LastCommunication' THEN LastCommunication END @sortOrder,\n    CASE WHEN @OrderBy = 'LastPreAlert'      THEN LastPreAlert END @sortOrder