(\n  SELECT \n    NULL AS newsTitle, \n    NULL AS newsBody, \n    DATE_FORMAT(review.postedOn, '%M %d, %Y') AS postedOn, \n    review.reviewSummary, \n    review.ourScore, \n    game.gameName, \n    game.gameImage\n  FROM game\n  JOIN review ON game.gameID = review.gameID\n  WHERE game.isPublished = 'y'\n)\nUNION ALL\n(\n  SELECT \n    news.newsTitle, \n    news.newsBody, \n    DATE_FORMAT(news.postedOn, '%M %d, %Y') AS postedOn, \n    NULL AS reviewSummary, \n    NULL AS ourScore, \n    NULL AS gameName, \n    NULL AS gameImage\n  FROM news\n)\nORDER BY postedOn DESC\nLIMIT 5;