CREATE PROCEDURE sp_EncryptData\n    @Data nvarchar(50)\nAS\nBEGIN\n    DECLARE @EncryptedData varbinary(50);\n    SET @EncryptedData = ENCRYPTBYKEY(KEY_GUID('mykey'), @Data);\n    RETURN @EncryptedData;\nEND;\n\nCREATE PROCEDURE sp_DecryptData\n    @EncryptedData varbinary(50)\nAS\nBEGIN\n    DECLARE @DecryptedData nvarchar(50);\n    SET @DecryptedData = DECRYPTBYKEY(@EncryptedData);\n    RETURN @DecryptedData;\nEND;