CREATE TRIGGER trg_MyTrigger ON MyTable FOR INSERT, UPDATE\nAS\nBEGIN\n    -- Impersonate a user with the necessary permissions\n    SETUSER 'MyTrustedUser'\n\n    -- Perform the actions that require elevated permissions\n    INSERT INTO TargetTable (Column1, Column2)\n    SELECT Column1, Column2 FROM inserted\n\n    -- Revert back to the original user context\n    SETUSER ''\nEND