SELECT *\nFROM your_table\nWHERE EXISTS (\n  SELECT 1\n  FROM information_schema.columns\n  WHERE table_name = 'your_table'\n  AND table_schema = 'your_database'\n  AND FIND_IN_SET(column_name, (\n    SELECT GROUP_CONCAT(column_name)\n    FROM information_schema.columns\n    WHERE table_name = 'your_table'\n    AND table_schema = 'your_database'\n  ))\n  AND CONCAT_WS('', your_table.column_name) LIKE '%your_value%'\n);