-- Create a queue and enqueue data on the sender side\nCREATE QUEUE sender_queue;\nBEGIN\n  DBMS_AQADM.ENQUEUE(\n    queue_name => 'sender_queue',\n    enqueue_options => SYS.DBMS_AQADM.ENQUEUE_OPTIONS_WAIT,\n    message_properties => SYS.DBMS_AQADM.MESSAGE_PROPERTIES_PRIORITY,\n    payload => SYS.ANYDATA.ConvertObject(sender_data),\n    msgid => message_id\n  );\nEND;\n\n-- Dequeue data on the receiver side\nCREATE QUEUE receiver_queue;\nBEGIN\n  DBMS_AQADM.DEQUEUE(\n    queue_name => 'receiver_queue',\n    dequeue_options => SYS.DBMS_AQADM.DEQUEUE_OPTIONS_WAIT,\n    message_properties => SYS.DBMS_AQADM.MESSAGE_PROPERTIES_PRIORITY,\n    payload => receiver_data,\n    msgid => message_id\n  );\nEND;