WITH parents AS (\n  SELECT animal_id, parent_male_id, parent_female_id\n  FROM animal\n  WHERE animal_id = 123  -- specify the animal ID here\n)\nSELECT *\nFROM (\n  SELECT *\n  FROM animal\n  START WITH animal_id IN (SELECT parent_male_id FROM parents)\n  CONNECT BY PRIOR animal_id = parent_male_id\n  UNION ALL\n  SELECT *\n  FROM animal\n  START WITH animal_id IN (SELECT parent_female_id FROM parents)\n  CONNECT BY PRIOR animal_id = parent_female_id\n)