DECLARE @BatchSize INT = 10000; -- adjust this value based on your system's performance\nDECLARE @TableName SYSNAME = 'YourTableName';\nDECLARE @ColumnName SYSNAME = 'YourColumnName';\n\nWHILE EXISTS (SELECT 1 FROM @TableName WHERE DATALENGTH(@ColumnName) > 0)\nBEGIN\n    BEGIN TRANSACTION;\n\n    UPDATE TOP (@BatchSize) @TableName\n    SET @ColumnName = @ColumnName\n    WHERE DATALENGTH(@ColumnName) > 0;\n\n    COMMIT TRANSACTION;\n\n    CHECKPOINT; -- periodically checkpoint to reduce transaction log growth\nEND;