CREATE TABLE myTable (id INT PRIMARY KEY, data VARCHAR(100));\n\n-- Create an index with a fill factor of 80\nCREATE INDEX myIndex ON myTable (id) WITH (FILLFACTOR = 80);\n\n-- Insert some data\nINSERT INTO myTable (id, data) VALUES (1, 'Initial data');\n\n-- Rebuild the index to apply the fill factor\nALTER INDEX myIndex ON myTable REBUILD;\n\n-- Check the page usage\nSELECT page_count, avg_page_space_used_in_percent\nFROM sys.dm_db_index_physical_stats(DB_ID(), OBJECT_ID('myTable'), NULL, NULL, NULL);