-- Create a test table\nCREATE TABLE t (fldA NUMBER PRIMARY KEY);\n\n-- Insert some data\nINSERT INTO t (fldA) VALUES (1);\nINSERT INTO t (fldA) VALUES (2);\n...\n\n-- Original dynamic SQL code\nDECLARE\n  c_Stmt  CONSTANT VARCHAR2(128) := 'DELETE FROM t WHERE fldA = :b1';\n  vSomeNumber NUMBER(10) := 1;\nBEGIN\n  FOR i IN 1 .. 9999999 LOOP\n    EXECUTE IMMEDIATE c_Stmt USING vSomeNumber;\n  END LOOP;\nEND;\n\n-- Rewritten code with regular DML statement\nDECLARE\n  vSomeNumber NUMBER(10) := 1;\nBEGIN\n  FOR i IN 1 .. 9999999 LOOP\n    DELETE FROM t WHERE fldA = vSomeNumber;\n  END LOOP;\nEND;\n\n-- Examine execution plans\nSET LINESIZE 120\nSET PAGESIZE 0\nSELECT * FROM TABLE(DBMS_XPLAN.DISPLAY_CURSOR(null, null, 'ALLSTATS LAST'));