CREATE TRIGGER trg_Employees_Delete ON Employees\nAFTER DELETE\nAS\nBEGIN\n    UPDATE v\n    SET IssuedBy = NULL\n    FROM Vouchers v\n    INNER JOIN deleted d ON v.IssuedBy = d.ObjectID\n\n    UPDATE v\n    SET ReceivedBy = NULL\n    FROM Vouchers v\n    INNER JOIN deleted d ON v.ReceivedBy = d.ObjectID\n\n    UPDATE v\n    SET ApprovedBy = NULL\n    FROM Vouchers v\n    INNER JOIN deleted d ON v.ApprovedBy = d.ObjectID\nEND