-- Oracle database table for ATM locations\nCREATE TABLE atm_locations (\n  id NUMBER PRIMARY KEY,\n  name VARCHAR2(50),\n  location SDO_GEOMETRY(2001, 4326) -- spatial column for geolocation\n);\n\n-- Insert sample data\nINSERT INTO atm_locations (id, name, location) VALUES (\n  1,\n  'ATM 1',\n  SDO_GEOMETRY(2001, 4326, SDO_POINT_TYPE(25.276987, 55.296249, NULL), NULL, NULL)\n);\n\n-- Oracle APEX page to display ATM locations on a map\nCREATE OR REPLACE PROCEDURE display_atm_locations\nIS\n  l_map OpenLayers.Map;\n  l_layer OpenLayers.Layer.Vector;\n  l_feature OpenLayers.Feature.Vector;\nBEGIN\n  -- Create map object\n  l_map := OpenLayers.Map('map', 'map_div');\n  \n  -- Create vector layer for ATM locations\n  l_layer := OpenLayers.Layer.Vector('ATM Locations', 'atm_locations');\n  \n  -- Loop through ATM locations and add features to the layer\n  FOR atm IN (SELECT * FROM atm_locations) LOOP\n    l_feature := OpenLayers.Feature.Vector(\n      atm.location,\n      atm.name,\n      'icon-star',\n      'red'\n    );\n    l_layer.addFeatures(l_feature);\n  END LOOP;\n  \n  -- Add layer to the map\n  l_map.addLayer(l_layer);\nEND;