-- Create a separate table to store the sequence values\nCREATE TABLE seq_values (\n  seq_name VARCHAR2(30),\n  next_val NUMBER,\n  increment_by NUMBER,\n  PRIMARY KEY (seq_name)\n);\n\n-- Insert the initial values for the sequence\nINSERT INTO seq_values (seq_name, next_val, increment_by)\nVALUES ('my_sequence', 1, 100);\n\n-- Create a procedure to fetch a batch of IDs\nCREATE OR REPLACE PROCEDURE fetch_ids(p_seq_name IN VARCHAR2, p_num_ids IN NUMBER)\nIS\n  v_next_val NUMBER;\n  v_increment_by NUMBER;\nBEGIN\n  -- Lock the sequence row to prevent concurrent updates\n  SELECT next_val, increment_by\n  INTO v_next_val, v_increment_by\n  FROM seq_values\n  WHERE seq_name = p_seq_name\n  FOR UPDATE;\n\n  -- Update the next value and increment by values\n  UPDATE seq_values\n  SET next_val = v_next_val + p_num_ids,\n    increment_by = v_increment_by + p_num_ids\n  WHERE seq_name = p_seq_name;\n\n  -- Return the batch of IDs\n  FOR i IN 1..p_num_ids LOOP\n    DBMS_OUTPUT.PUT_LINE(v_next_val + i - 1);\n  END LOOP;\nEND;