SELECT ...\nFROM (\n  Participant PAR\n  INNER JOIN Individual IND ON PAR.APETSID = IND.APETSID\n)\nINNER JOIN (\n  Ethnicity ETH\n  INNER JOIN Education EDU ON IND.EthnicityID = ETH.ID\n) ON IND.EducationID = EDU.ID\nINNER JOIN (\n  Marital MAR\n  INNER JOIN Participant-Probation PXP ON IND.Marital = MAR.ID\n) ON PAR.ID = PXP.ParticipantID\nINNER JOIN (\n  Probation PBN\n  INNER JOIN Class-Participant CXP ON PXP.ProbationID = PBN.ID\n) ON PAR.ID = CXP.ParticipantID\nINNER JOIN (\n  Class CLS\n  INNER JOIN Official OFR ON CXP.ClassID = CLS.ID\n) ON PAR.ReferringPO = OFR.ID\nINNER JOIN (\n  Participant-Official PXO\n  INNER JOIN Official OFA ON PAR.ID = PXO.ParticipantID\n) ON PXO.OfficialID = OFA.ID