CREATE TABLE ASPNET_SESSIONS (\n  SESSIONID VARCHAR2(80) PRIMARY KEY,\n  APPLICATIONID VARCHAR2(80) NOT NULL,\n  CREATED DATETIME NOT NULL,\n  EXPIRES DATETIME NOT NULL,\n  LOCKED NUMBER(1) NOT NULL,\n  LOCKDATE DATETIME,\n  LOCKOWNER VARCHAR2(80),\n  SESSIONITEMS BLOB\n);\n\nCREATE TABLE ASPNET_APPLICATIONS (\n  APPLICATIONID VARCHAR2(80) PRIMARY KEY,\n  APPLICATIONNAME VARCHAR2(80) NOT NULL\n);