SELECT \n    s.Sale_ID,\n    s.Product_ID,\n    s.Sale_Date,\n    p.Price\nFROM \n    Sales s\nINNER JOIN \n    Prices p \nON \n    s.Product_ID = p.Product_ID \n    AND s.Sale_Date >= p.Sale_Date\nWHERE \n    NOT EXISTS (SELECT 1 \n                FROM Prices p2 \n                WHERE p2.Product_ID = p.Product_ID \n                  AND p2.Sale_Date > p.Sale_Date \n                  AND p2.Sale_Date <= s.Sale_Date)