-- Configure Transactional Replication for reporting server\nCREATE PUBLICATION [ReportPub] ON [YourDatabase]\nFOR TRANSACTIONAL REPLICATION\n(\n    @PublicationType = N'Transactional',\n    @Publisher = N'YourClusterName',\n    @PublisherDB = N'YourDatabase',\n    @Subscriber = N'ReportingServerName',\n    @SubscriberDB = N'ReportingDatabase'\n);\n\n-- Configure Database Mirroring for OLTP cluster\nCREATE ENDPOINT [MirroringEndpoint] ON [YourDatabase]\nFOR DATABASE_MIRRORING\n(\n    ROLE = PARTNER,\n    AUTHENTICATION = 'Windows Authentication',\n    ENCRYPTION = ENABLED\n);\n\nALTER DATABASE [YourDatabase] SET PARTNER ('TCP://YourMirrorServerName:5022');