SELECT \n  days_of_months.`Date`,\n  m.NAME AS "Model",\n  COUNT(t.ID) AS "Count"\nFROM \n  (SELECT DATE(CONCAT(2009, '-', temp_months.id, '-', temp_days.id)) AS "Date"\n   FROM temp_months\n   INNER JOIN temp_days ON temp_days.id <= temp_months.last_day\n   WHERE temp_months.id = 3 -- March\n  ) days_of_months\n  CROSS JOIN MODEL m\n  LEFT JOIN APPLIANCE_UNIT a ON (m.ID = a.MODEL_FK AND a.NUMBER_OF_UNITS > 0)\n  LEFT JOIN NEW_TICKET t ON (a.NEW_TICKET_FK = t.ID AND t.TYPE = 'SALES' AND t.SALES_ORDER_FK IS NOT NULL)\n  AND DATE(t.CREATION_DATE_TIME) = DATE(days_of_months.`Date`)\nGROUP BY \n  days_of_months.`Date`, m.ID, m.NAME