DECLARE\n  v_original_schema VARCHAR2(30);\nBEGIN\n  v_original_schema := SYS_CONTEXT('USERENV', 'CURRENT_SCHEMA');\n  \n  -- Change the schema\n  EXECUTE IMMEDIATE 'ALTER SESSION SET CURRENT_SCHEMA=new_schema';\n  \n  -- Perform some other statements...\n  \n  -- Revert back to the original schema\n  EXECUTE IMMEDIATE 'ALTER SESSION SET CURRENT_SCHEMA=' || v_original_schema;\nEND;