UPDATE history_table\nSET some_date_col = \n    TO_CHAR(\n        COALESCE(\n            TO_DATE(some_date_col, 'yyyy-mm-dd'),\n            TO_DATE(some_date_col, 'mm/dd/yyyy'),\n            TO_DATE(some_date_col, 'dd/mm/yy'),\n            TO_DATE(some_date_col, 'mm/dd/yy'),\n            TO_DATE(some_date_col, 'dd/mm/yyyy')\n        ) + 30, \n        'mm/dd/yyyy'\n    )\nWHERE some_date_col IS NOT NULL;