DECLARE\n    v_start_time NUMBER;\n    v_end_time NUMBER;\n    v_input RAW(30*1024*1024); -- 30 MB\n    v_hash RAW(20); -- SHA-1 hash size\n    v_size NUMBER;\nBEGIN\n    FOR i IN 2..15 LOOP -- From 2MB to 30MB in steps of 2MB\n        v_size := i * 1024 * 1024; -- Calculate size in bytes\n        v_input := UTL_RAW.COPY(UTL_RAW.CAST_TO_RAW('A'), 1, v_size); -- Fill input with 'A'\n        \n        v_start_time := DBMS_UTILITY.GET_TIME(); -- Record start time\n        \n        -- Compute SHA-1 hash\n        v_hash := DBMS_CRYPTO.HASH(v_input, DBMS_CRYPTO.HASH_SH1);\n        \n        v_end_time := DBMS_UTILITY.GET_TIME(); -- Record end time\n        \n        DBMS_OUTPUT.PUT_LINE('Input size (MB): ' || TO_CHAR(i*2) || ', Time taken (seconds): ' || TO_CHAR((v_end_time - v_start_time)/100));\n    END LOOP;\nEND;