DECLARE\n    TYPE AttrValueRec IS RECORD (\n        attr        VARCHAR2(40),\n        val         VARCHAR2(2000),\n        inst        NUMBER(4)\n    );\n\n    SUBTYPE AttrValueType IS AttrValueRec.attr%TYPE;\n    SUBTYPE ValType IS AttrValueRec.val%TYPE;\n    SUBTYPE InstType IS AttrValueRec.inst%TYPE;\n\n    FUNCTION create_attrval(attr   AttrValueType,\n                            val    ValType,\n                            inst   InstType := 1)\n    RETURN AttrValueRec IS\n        attr_value  AttrValueRec;\n    BEGIN\n        attr_value.attr := attr;\n        attr_value.val := val;\n        attr_value.inst := inst;\n        RETURN attr_value;\n    END;\nBEGIN\n    NULL;\nEND;