CREATE OR REPLACE PROCEDURE sqlout(query IN VARCHAR2)\nIS\n  c NUMBER := DBMS_SQL.OPEN_CURSOR;\n  stmt VARCHAR2(4000);\nBEGIN\n  stmt := 'CREATE GLOBAL TEMPORARY TABLE tmp_tab AS (' || query || ')';\n  DBMS_SQL.PARSE(c, stmt, DBMS_SQL.NATIVE);\n  DBMS_SQL.EXECUTE(c);\n  DBMS_SQL.CLOSE_CURSOR(c);\nEND;