CREATE OR REPLACE FUNCTION unique_check()\nRETURNS TRIGGER AS $$\nBEGIN\n    IF EXISTS (\n        SELECT 1\n        FROM (\n            SELECT * FROM child_table1\n            UNION ALL\n            SELECT * FROM child_table2\n            UNION ALL\n            ...\n        ) AS combined_table\n        WHERE combined_table.column_name = NEW.column_name\n    ) THEN\n        RAISE EXCEPTION 'Duplicate value in column_name';\n    END IF;\n    RETURN NEW;\nEND;\n$$ LANGUAGE plpgsql;\n\nCREATE TRIGGER unique_check_trigger\nBEFORE INSERT OR UPDATE ON parent_table\nFOR EACH ROW\nEXECUTE PROCEDURE unique_check();