// Queue table schema\nCREATE TABLE email_queue (\n  id INT PRIMARY KEY,\n  type ENUM('transactional', 'newsletter'),\n  email TEXT,\n  priority INT,\n  created_at TIMESTAMP\n);\n\n// Inserting an email into the queue\n$type = 'newsletter'; // or 'transactional'\n$priority = 2; // adjust priority based on type and requirements\n$email = 'recipient@example.com';\n$query = "INSERT INTO email_queue (type, email, priority) VALUES ('$type', '$email', $priority)";