DECLARE\n  l_http_request  UTL_HTTP.REQ;\n  l_http_response UTL_HTTP.RESP;\n  l_buffer        CLOB;\n  l_url           VARCHAR2(200) := 'http://example.com/soap/service';\n  l_soap_request  VARCHAR2(2000) := '<soap:Envelope xmlns:soap="http://www.w3.org/2003/05/soap-envelope/">...</soap:Envelope>';\nBEGIN\n  UTL_HTTP.SET_PROXY('http://your_proxy_server:port');\n  \n  l_http_request := UTL_HTTP.BEGIN_REQUEST(l_url, 'POST', UTL_HTTP.HTTP_VERSION_1_1);\n  UTL_HTTP.SET_HEADER(l_http_request, 'Content-Type', 'text/xml; charset=utf-8');\n  UTL_HTTP.SET_HEADER(l_http_request, 'SOAPAction', 'your_soap_action');\n  \n  UTL_HTTP.WRITE_TEXT(l_http_request, l_soap_request);\n  \n  l_http_response := UTL_HTTP.GET_RESPONSE(l_http_request);\n  \n  UTL_HTTP.READ_TEXT(l_http_response, l_buffer);\n  \n  DBMS_OUTPUT.PUT_LINE(l_buffer);\n  \n  UTL_HTTP.END_RESPONSE(l_http_response);\nEXCEPTION\n  WHEN UTL_HTTP.END_OF_BODY THEN\n    UTL_HTTP.END_RESPONSE(l_http_response);\nEND;