CREATE OR REPLACE PROCEDURE test_refcur\nIS\n   refCursorValue SYS_REFCURSOR;\n   TYPE cursrow_tab IS TABLE OF dashboard_package.visits%ROWTYPE;\n   cursrow_list cursrow_tab;\nBEGIN\n   dashboard_package.visits(refCursorValue, 12345);\n   FETCH refCursorValue BULK COLLECT INTO cursrow_list;\n   FOR i IN 1..cursrow_list.COUNT\n   LOOP\n      DBMS_OUTPUT.PUT_LINE(cursrow_list(i).ytd_tot);\n   END LOOP;\nEND test_refcur;