CREATE OR REPLACE PACKAGE eng_metric\nIS\n  PROCEDURE convert(degree_fahrenheit  IN OUT NUMBER, degree_celsius  IN OUT NUMBER, measure  IN VARCHAR2);\n\n  PROCEDURE convert(liters  IN OUT NUMBER, gallons  IN OUT NUMBER);\nEND eng_metric;\n/\n\nCREATE OR REPLACE PACKAGE BODY eng_metric\nAS\n  PROCEDURE Convert\n       (degree_fahrenheit  IN OUT NUMBER,\n        degree_celsius     IN OUT NUMBER,\n        measure            IN VARCHAR2)\n  IS\n    df         NUMBER;\n    dc         NUMBER;\n    convertf   NUMBER;\n    measurecf  VARCHAR2(4);\n  BEGIN\n    measurecf := measure;\n\n    df := degree_fahrenheit;\n\n    dc := degree_celsius;\n\n    IF measure = 'TEMP' THEN\n      IF dc IS NULL THEN\n        convertf := ((df - 32) * .56);\n\n        degree_celsius := convertf;\n\n        dbms_output.Put_line('The temperature in celsius is '\n                             ||To_char(degree_celsius));\n      ELSIF df IS NULL THEN\n        convertf := (dc + 17.98) * 1.8;\n\n        degree_fahrenheit := convertf;\n      END IF;\n    ELSE\n      dbms_output.Put_line('Invalid measure');\n    END IF;\n  END convert;\n\n  PROCEDURE Convert\n       (liters   IN OUT NUMBER,\n        gallons  IN OUT NUMBER)\n  IS\n    lit        NUMBER;\n    gal        NUMBER;\n    convertlg  NUMBER;\n  BEGIN\n    lit := liters;\n\n    gal := gallons;\n\n    IF gal IS NULL THEN\n      convertlg := (lit / 3.785);\n\n      gallons := convertlg;\n    ELSIF lit IS NULL THEN\n      convertlg := (gal * 3.785);\n\n      liters := convertlg;\n    END IF;\n  END convert;\nEND eng_metric;\n/\n\nDECLARE\n  liters   NUMBER := 25;\n  gallons  NUMBER := NULL;\nBEGIN\n  eng_metric.Convert(liters, gallons);\n\n  dbms_output.Put_line(To_char(gallons));\nEND;\n/