CREATE OR REPLACE PROCEDURE my_procedure(p_schema IN VARCHAR2)\nAS\n  my_cursor SYS_REFCURSOR;\n  v_sql     VARCHAR2(1000);\nBEGIN\n  v_sql := 'SELECT my_field FROM ' || p_schema || '.my_table';\n\n  OPEN my_cursor FOR v_sql;\n\n  -- Now you can loop through the cursor\n  FOR rec IN my_cursor LOOP\n    -- Process the record\n    DBMS_OUTPUT.PUT_LINE(rec.my_field);\n  END LOOP;\n\n  CLOSE my_cursor;\nEND;