CREATE OR REPLACE PACKAGE logging AS\n  g_logging_enabled  BOOLEAN := FALSE;\n\n  PROCEDURE log(p_message IN VARCHAR2);\n  PROCEDURE enable_logging;\n  PROCEDURE disable_logging;\nEND logging;\n\nCREATE OR REPLACE PACKAGE BODY logging AS\n  PROCEDURE log(p_message IN VARCHAR2) IS\n  BEGIN\n    IF g_logging_enabled THEN\n      -- logging code here\n      DBMS_OUTPUT.PUT_LINE(p_message);\n    END IF;\n  END log;\n\n  PROCEDURE enable_logging IS\n  BEGIN\n    g_logging_enabled := TRUE;\n  END enable_logging;\n\n  PROCEDURE disable_logging IS\n  BEGIN\n    g_logging_enabled := FALSE;\n  END disable_logging;\nEND logging;