DECLARE\n  v_owner      VARCHAR2(128) := 'SCHEMA_USER';\n  v_tablename  VARCHAR2(128) := 'TABLENAME';\n  v_sql        VARCHAR2(4000);\n  v_cursor     INTEGER;\n  v_tab        SYS_REFCURSOR;\n\nBEGIN\n  v_sql := 'SELECT * FROM ' || v_owner || '.' || v_tablename;\n  v_cursor := DBMS_SQL.OPEN_CURSOR;\n  DBMS_SQL.PARSE(v_cursor, v_sql, DBMS_SQL.NATIVE);\n  v_tab := DBMS_SQL.EXECUTE(v_cursor);\n  -- Process the results\n  DBMS_SQL.CLOSE_CURSOR(v_cursor);\nEND;